    emaValue = prices[i] * k + emaValue * (1 - k);
  }
  return parseFloat(emaValue.toFixed(2));
}

function rsi(prices, period = 14) {
  if (!prices || prices.length < period + 1) return null;

  const changes = [];
  for (let i = 1; i < prices.length; i++) {
    changes.push(prices[i] - prices[i - 1]);
  }

  let gains = 0;
  let losses = 0;

  for (let i = 0; i < period && i < changes.length; i++) {
    if (changes[i] > 0) gains += changes[i];
    else losses -= changes[i];
  }

  let avgGain = gains / period;
  let avgLoss = losses / period;

  for (let i = period; i < changes.length; i++) {
    if (changes[i] > 0) {
      avgGain = (avgGain * (period - 1) + changes[i]) / period;
      avgLoss = (avgLoss * (period - 1)) / period;
    } else {
      avgGain = (avgGain * (period - 1)) / period;
      avgLoss = (avgLoss * (period - 1) - changes[i]) / period;
    }
  }

  if (avgLoss === 0) return 100;
  const rs = avgGain / avgLoss;
  return parseFloat((100 - (100 / (1 + rs))).toFixed(2));
}

function macd(prices) {
  if (!prices || prices.length < 26) return null;
  const ema12 = ema(prices, 12);
  const ema26 = ema(prices, 26);
  if (!ema12 || !ema26) return null;

  return {
    macd: parseFloat((ema12 - ema26).toFixed(2)),
    ema12: ema12,
    ema26: ema26
  };
}

function bollingerBands(prices, period = 20, stdDev = 2) {
  if (!prices || prices.length < period) return null;
  const smaValue = sma(prices, period);
  if (!smaValue) return null;

  const recentPrices = prices.slice(-period);
  const variance = recentPrices.reduce((sum, price) => 
    sum + Math.pow(price - smaValue, 2), 0) / period;
  const standardDeviation = Math.sqrt(variance);

  return {
    upper: parseFloat((smaValue + (stdDev * standardDeviation)).toFixed(2)),
    middle: smaValue,
    lower: parseFloat((smaValue - (stdDev * standardDeviation)).toFixed(2)),
    bandwidth: parseFloat((2 * stdDev * standardDeviation).toFixed(2))
  };
}

function stochastic(highs, lows, closes, period = 14) {
  if (!highs || !lows || !closes || closes.length < period) return null;

  const recentHighs = highs.slice(-period);
  const recentLows = lows.slice(-period);
  const currentClose = closes[closes.length - 1];

  const highestHigh = Math.max(...recentHighs);
  const lowestLow = Math.min(...recentLows);

  if (highestHigh === lowestLow) return null;

  const k = ((currentClose - lowestLow) / (highestHigh - lowestLow)) * 100;
  return parseFloat(k.toFixed(2));
}

function vwap(highs, lows, closes, volumes, period = 20) {
  if (!highs || !lows || !closes || !volumes || closes.length < period) return null;

  const startIdx = Math.max(0, closes.length - period);
  let sumPriceVolume = 0;
  let sumVolume = 0;

  for (let i = startIdx; i < closes.length; i++) {
    const typicalPrice = (highs[i] + lows[i] + closes[i]) / 3;
    sumPriceVolume += typicalPrice * volumes[i];
    sumVolume += volumes[i];
  }

  return sumVolume > 0 ? parseFloat((sumPriceVolume / sumVolume).toFixed(2)) : null;
}

function atr(highs, lows, closes, period = 14) {
  if (!highs || !lows || !closes || closes.length < period + 1) return null;

  const trueRanges = [];
  for (let i = 1; i < closes.length; i++) {
    const highLow = highs[i] - lows[i];
    const highClose = Math.abs(highs[i] - closes[i - 1]);
    const lowClose = Math.abs(lows[i] - closes[i - 1]);
    trueRanges.push(Math.max(highLow, highClose, lowClose));
  }

  if (trueRanges.length < period) return null;
  const avgTrueRange = trueRanges.slice(-period).reduce((a, b) => a + b, 0) / period;
  return parseFloat(avgTrueRange.toFixed(2));
}

